insert into at_replynotice (at_rno, at_bno, at_replytext, at_replier, at_regdate,id) values
(1,1,'우와, 식물에 진심인분을 만났듯 합니다. 저도 식물 키우는 것을 좋아해서 하나둘 늘어났는데, 한 50개 정도? 키우고 있어요. 그런데 주로 물을 잘 안주어도 잘 자라는 애들만 살아 남았죠. 더 잘 키우고 싶은데, 딱이다 싶은 분이시네요. 회사동료들과 함께 이야기 나누고 싶은 글이었습니다. 넘 감사해요~~',
insert into at_replynotice (at_rno, at_bno, at_replytext, at_replier, at_regdate,id) values
(2,1,'재활용품을 활용해서 식물을 키우는 아이디에 기발한 것 같아요, 따라 해봐야겠어요!','아름',to_date('2022-12-09 17:57','yyyy.mm.dd hh24:mi'),'t1');
insert into at_replynotice (at_rno, at_bno, at_replytext, at_replier, at_regdate,id) values
(3,1,'제가 좋아하는 식물을 그린어스 님도 키우고 있었다니 동질감이 들고 반갑네요ㅎㅎ 식친들끼리는 통하는 것이 있죠!',
insert into at_replynotice (at_rno, at_bno, at_replytext, at_replier, at_regdate,id) values
(4,2,'아날로그의 덜컹거림과 불규칙함을 사랑한다는 표현이 와닿네요. 저도 아날로그 취미를 즐기는 사람인데요. 요즘은 필름 카메라와 바이닐 수집, 카세트 테이프 수집을 하고 있어요. 가끔은 "넌 이렇게 발전한 시대에 살면서 왜 불편한 오래된 것들에 집착하냐"는 쓴소리를 듣게도 하지만 이런 불편함을 감수하고도 좋은 걸 어쩌겠나 싶어요. 이들이 주는 물성이 저를 더 설레게 하는 것 같아요. 언젠가부터 데이터로 처리되어 사실상 무형이 되어버린 사진과 음악을 만지고 느껴보고 싶은 마음 때문일까요? 아무튼 요즘 저는 풍요로운 취미생활을 하기 위해 돈도 열심히 벌고, 그러면서 더 삶을 풍성하게 살아가는 것 같아요. ;) 끝으로 제 필름 촬영 결과물 몇장 남기고 갑니다. 모두 즐거운 취미 생활 하시길!-',
insert into at_replynotice (at_rno, at_bno, at_replytext, at_replier, at_regdate,id) values
(5,2,'와 갖고 계신 카메라들을 보니 진짜 진심이시네요. 아날로그 카메라 보기만 해도 멋져요.','대우',to_date('2022-12-09 11:39','yyyy.mm.dd hh24:mi'),'t1');
